package leetcode;

import org.junit.Test;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.Collections;
import java.util.HashMap;
import java.util.List;
import java.util.Map;
import java.util.stream.Collectors;
import java.util.stream.IntStream;

import static org.junit.Assert.assertEquals;

/**
 * Created by jeremy on 2020/11/24.
 */
public class FindAllAnagramsInString {
    public static List<Integer> findAnagrams(String s, String p) {
        if (s == null || s.length() == 0) return Collections.emptyList();
        if (p == null || p.length() == 0) return Collections.emptyList();
        if (s.length() < p.length()) return Collections.emptyList();

        List<Boolean> indexes = findAnagrams(s, p, 0, new ArrayList<>());
        return IntStream.range(0, indexes.size())
            .filter(indexes::get)
            .boxed()
            .collect(Collectors.toList());
    }

    public static List<Boolean> findAnagrams(String s, String p, int index, List<Boolean> acc) {
        if (index + p.length() > s.length()) {
            return acc;
        }

        String target = s.substring(index, index + p.length());
        boolean isAnagram = countAlphabetMap(target).equals(countAlphabetMap(p));
        if (isAnagram) {
            while (isSkipIndex(s, p, index)) {
                acc.add(true);
                index++;
            }
            acc.add(true);
            return findAnagrams(s, p, index + 1, acc);
        }
        while (isSkipIndex(s, p, index)) {
            acc.add(false);
            index++;
        }

        acc.add(false);
        return findAnagrams(s, p, index + 1, acc);
    }

    public static boolean isSkipIndex(String s, String p, int index) {
        return index + p.length() < s.length() &&               // inner index
            s.charAt(index) == s.charAt(index + p.length());    // same with next character
    }

    public static Map<Character, Integer> countAlphabetMap(String s) {
        Map<Character, Integer> countMap = new HashMap<>();
        for (Character p : s.toCharArray()) {
            countMap.put(p, countMap.getOrDefault(p, 0) + 1);
        }
        return countMap;
    }

    public static class UnitTest {
        @Test
        public void test1() {
            List<Integer> expected = Arrays.asList(0,6);
            List<Integer> actual = findAnagrams("cbaebabacd", "abc");
            assertEquals(expected, actual);
        }

        @Test
        public void test2() {
            List<Integer> expected = Arrays.asList(0,1,2);
            List<Integer> actual = findAnagrams("abab", "ab");
            assertEquals(expected, actual);
        }

        @Test
        public void test3() {
            List<Integer> expected = Arrays.asList(0,10001);
            List<Integer> actual = findAnagrams(
                "",
                ""
            );
            assertEquals(expected, actual);
        }
    }
}
